mapper.select:
		SELECT * FROM cms_proxy.cj_articles WHERE primaryCategoryId =#{categoryId} 
		
    	
      		AND seoSlug LIKE #{pattern}
		
		
      		LIMIT #{limit}

SELECT id, author, headline, shortHeadline, englishTitle, summary, imageUrl, imageCaption, primaryCategoryId, primaryCategoryName, primaryCategoryTeluguLabel, primaryCategorySeoSlug, primaryCategoryUrl, primarySubCategoryId, primarySubCategoryName, primarySubCategoryTeluguLabel, primarySubCategorySeoSlug, primarySubCategoryUrl, seoSlug, metaTitle, url, ampUrl, contentType, createdAt, updatedAt, publishedAtSm FROM ( ( SELECT * FROM cms_proxy.cj_articles WHERE primaryCategoryId =#{categoryId} AND primarysubcategoryid =#{subCategoryId} AND published = true
AND contentType=#{contentType}
UNION SELECT * FROM cms_proxy.cj_liveblogs WHERE primaryCategoryId =#{categoryId} AND primarysubcategoryid =#{subCategoryId} AND published = true
AND contentType=#{contentType}
) ORDER BY updatedAt DESC
LIMIT #{limit}
)A

